import React from "react";
import { Pane, Text, Button, Heading, Paragraph, TextInputField, FormField } from "evergreen-ui";
import { Link } from "react-router-dom";
import background from "./Img/lib2.png";

function LoginPage() {
  return (
    <div>
      <Pane backgroundImage = {`url(${background})`} display="flex" alignItems="center" justifyContent="center" padding={100} flexDirection="column">
        <Heading fontSize={100} color = {'white'}>SmartLib</Heading>
        <Text size={600} color = {'white'}><br></br><br></br><br></br>Where student meet seats</Text>
      </Pane>
      <Pane height={1000} backgroundColor="beige" display="flex" justifyContent="center" padding={20}>
        <FormField>
        <Heading size={800} >Login Page</Heading>
        <Text><br></br></Text>
        <TextInputField
          id="Login Information"
          required
          label="School Email"
          placeholder="username@e.ntu.edu.sg"
          inputHeight = {45}
          inputWidth = {450}
        />
        <TextInputField
          id="Password Information"
          required
          label="Password"
          inputHeight = {45}
          inputWidth = {450}
        />
        <Pane display="flex" justifyContent="right">
          <Link to="/MyBookings" style={{ textDecoration: "none" }}> {/* textDecoration has to set to none or else the button will have a line below the Login text */}
              <Button appearance="primary" intent="success" height={48}>Login</Button>
          </Link>
          <Link to="/MyBookings">Forget Password?</Link>
        </Pane>
        
        <Pane display="flex" padding={16}  borderRadius={3}>
          <Pane flex={1} alignItems="center" display="flex">
            <Heading size={600}>Left Aligned</Heading>
          </Pane>
          <Pane>
            {/* Below you can see the marginRight property on a Button. */}
            <Button marginRight={16}>Button</Button>
            <Button appearance="primary">Primary Button</Button>
          </Pane>
        </Pane>

        </FormField>
      </Pane>
    </div>
  );
}

export default LoginPage;
