import React from "react";
import { Pane, Text, Button, Heading, Paragraph, TextInputField, FormField, Avatar, BackButton } from "evergreen-ui";
import { Link } from "react-router-dom";
import background from "./Img/Stress.png";

function SeatInformationPage() {
  return (
    <div>
      <Pane paddingBottom={20}>
          <Link to="/HomePage" style={{ textDecoration: "none" }}> {/* textDecoration has to set to none or else the button will have a line below the Login text */}
            <BackButton marginLeft={16} marginTop={16} intent="success" height={48}/>
          </Link>
      </Pane>
      
      
      <Pane display="flex" padding={16}  borderRadius={3}>
        <Pane>
          <Avatar isSolid name="Jeroen Ransijn" size={300} marginLeft={100} marginTop={20} />
        </Pane>
        <Pane flex={1} alignItems="start" display="flex">
          <Heading size={600} marginLeft={70}>Left Aligned</Heading>
        </Pane>
      </Pane>

      <Pane height={1000} backgroundColor="beige" display="flex" justifyContent="center" padding={20}>
        <FormField>
        <Heading size={800} >Password Reset</Heading>
        <Text><br></br></Text>
        <TextInputField
          id="Login Information"
          required
          label="School Email"
          placeholder="username@e.ntu.edu.sg"
          inputHeight = {45}
          inputWidth = {450}
        />
        {/* This is the container/pane for the login section */}
        <Pane display="flex" borderRadius={3}>
          <Pane flex={1} alignItems="center" display="flex">
            <Link to="/MyBookings">Forget Password?</Link>
          </Pane>
          <Pane paddingBottom={20}>
            <Link to="/HomePage" style={{ textDecoration: "none" }}> {/* textDecoration has to set to none or else the button will have a line below the Login text */}
                <Button appearance="primary" height={48} iconBefore>Send Reset Email</Button>
            </Link>
          </Pane>
        </Pane>
        </FormField>
      </Pane>
    </div>
  );
}

export default SeatInformationPage;
