import React, { useState } from "react";
import { Pane, Text, Button, Heading, Paragraph, TextInput, TextInputField, FormField } from "evergreen-ui";
import { Link } from "react-router-dom";
import background from "./Img/c1.png";
import { userData } from "./JsonTestFiles/registeredUser";

function CreateAccountPage(props) {

  const [state, setState] = useState({ name: "", email: "", password: "", confirmPassword: "" }) //Initial Local state

  const handleChange = (e) => {
    const { id, value } = e.target
    setState(prevState => ({
      ...prevState,
      [id]: value
    }))
  }
  const handleSubmitClick = (e) => {
    e.preventDefault();
    if (state.password === state.confirmPassword) {
      const fs = require("fs");
      let usersjson = fs.readFileSync("registeredUser.json", "utf-8");
      let users = JSON.parse(usersjson);
      users.push({
        name: state.name,
        email: state.email,
        password: state.password
      });
      usersjson = JSON.stringify(users);
      fs.writeFileSync("registeredUser.json", usersjson, "utf-8");
    } else {
      props.showError('Passwords do not match');
    }
  }

  return (
    <div>
      <Pane backgroundImage={`url(${background})`} display="flex" alignItems="center" justifyContent="center" padding={100} flexDirection="column">
        <Heading fontSize={100} color={'white'}>Create Account</Heading>
        <Text size={600} color={'white'}><br></br><br></br><br></br>Making life simpler for you!</Text>
      </Pane>
      <Pane height={1000} backgroundColor="beige" display="flex" justifyContent="center" padding={20}>
        <form>
          <Heading size={800} >Create New Account</Heading>
          <Text><br></br></Text>
          <TextInputField
            id="Name Information"
            required
            label="Name"
            placeholder="Jacob Ballas"
            inputHeight={45}
            inputWidth={450}
            value={state.name}
            onChange={handleChange}
          />
          <TextInputField
            id="Login Information"
            required
            label="School Email"
            placeholder="username@e.ntu.edu.sg"
            inputHeight={45}
            inputWidth={450}
            type="email"
            value={state.email}
            onChange={handleChange}
          />
          <TextInputField
            id="Password Information"
            required
            label="Password"
            secureTextEntry={true}
            inputHeight={45}
            inputWidth={450}
            type="password"
            value={state.password}
            onChange={handleChange}
          />
          <TextInputField
            id="Double confirm Password"
            required
            label="Confirm Password"
            inputHeight={45}
            inputWidth={450}
            type="password"
            value={state.confirmPassword}
            onChange={handleChange}
          />
          {/* This is the container/pane for the login section */}
          <Pane display="flex" borderRadius={3}>
            <Pane flex={1} alignItems="center" display="flex">
              <Link to="/ForgetPassword">Forget Password?</Link>
            </Pane>
            <Pane paddingBottom={20}>
              <Button appearance="primary" height={48} type="submit" onClick={handleSubmitClick}>Create Account</Button>
              {/* <Link to="/Login" style={{ textDecoration: "none" }}> {/* textDecoration has to set to none or else the button will have a line below the Login text
                
              </Link> */}
            </Pane>
          </Pane>
        </form>
      </Pane>
    </div>
  );
}

const readData = () => {
  return (
    <div className="userData">
      {userData.map((data, key) => {
        return (
          <div key={key}>
            <Stock
              key={key}
              name={data.name}
              email={data.email}
              password={data.password}
            />
          </div>
        );
      })}
    </div>
  );
};

export default CreateAccountPage;
